import React, { useEffect, useState } from "react";
import axios from "axios";
import Profile from "../Components/users/Profile";
import Course from "../path-to-your-course-component"; // Replace with the actual path
import { useCookies } from "react-cookie";
import { useNavigate } from "react-router-dom";
import Blog from "../Components/users/Blog";

const Account = () => {
  const [user, setUser] = useState([]);
  const [page, setPage] = useState("profile");
  const [photoPreview, setPhotoPreview] = useState(null);
  const [cookies, setCookie, removeCookie] = useCookies(['token']);
  const history = useNavigate();

  // fetch user data
  useEffect(() => {
    axios
      .get("http://localhost:5000/user")
      .then((response) => {
        setUser(response.data);
        setPhotoPreview(response.data.profile_image_name);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  }, []);

  function logout() {
    removeCookie('token');
    history("/");
  }

  // to open and close sidebar
  const [isSideOpen, setIsSideOpen] = useState(true);
  const [position, setPosition] = useState("left-0");

  function openSideBar() {
    if (isSideOpen) {
      setPosition("-left-64");
    } else {
      setPosition("left-0");
    }
    setIsSideOpen(!isSideOpen);
  }
  // to open and close sidebar //end

  return (
    <div className="flex flex-wrap">
      {/* sidebar */}
      <div className="relative w-[260px] h-full">
        {/* ... (your existing sidebar code) */}
      </div>
      {/* content */}
      <div className={`${page === "profile" ? "block" : "hidden"} w-full`}>
        <Profile />
      </div>
      <div className={`${page === "Course" ? "block" : "hidden"} w-full`}>
        <Course />
      </div>
      <div className={`${page === "Blog" ? "block" : "hidden"} w-full`}>
        <Blog />
      </div>
    </div>
  );
};

export default Account;
